import AddBar from "./components/addBar";
import ShowBooks from "./components/showBook";
import SearchImage from "./components/api";
import { useState } from 'react'

function App() {
  const [bookList,setBookList]=useState([])

  const handleSubmit=(value)=>{
    const image=SearchImage(value)
    const imgadd=image[0].urls.small
    console.log(image)
    setBookList([...bookList,{name:value,id:value}])
  }
  const deleteBook=(id)=>{
    const updatedList=bookList.filter((book)=>{
      return book.id!==id
    })
    setBookList(updatedList)
  }

  return (
    <div>
      <AddBar onSubmit={handleSubmit}/>
      <ShowBooks bookList={bookList} onDel={deleteBook} />
    </div>
  );
}

export default App;
