import EditComponent from "./editComponent"
import { useState } from 'react'

const Book=({bookDetails,onDel,onEdit})=>{
    const [editState,setEditState]=useState(true)

    const handleDeleteClick=()=>{
        onDel(bookDetails.id)
    }
    const handleEditChange=()=>{
        setEditState(!editState)
    }
    const handleEdit=(id,newName)=>{
        onEdit(id,newName)
        setEditState(!editState)
    }

    const show=editState?(<div>{bookDetails.name}</div>):(<EditComponent bookDetails={bookDetails} onEdit={handleEdit} />)
    return(
        <div className="card" >
            <img src={bookDetails.imgadd} />
            {show}
            <button className="button is-rounded" onClick={handleDeleteClick} >Delete</button>
            <button className="button is-rounded" onClick={handleEditChange} >Edit</button>

        </div>
    )
}

export default Book
