const AddBar=()=>{
    const [text,setText]=useState('')
    const handleChange=(event)=>{
        setText(event.target.value)
    }
    return(
        <div>
            <form>
                <input value={text} placeholder='Book name' onChange={handleCha}/>
                <button>Add book</button>

            </form>
        </div>
    )
}

export default AddBar
